        <span className="text-gray-600">Preparando pagamento...</span>
      </div>
    )
  }

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {/* Card Input */}
      <div>
        <label className="block text-sm font-medium text-gray-700 mb-3">
          Informa√ß√µes do Cart√£o
        </label>
        <div className="border border-gray-300 rounded-md p-4 bg-white">
          <CardElement
            options={{
              style: {
                base: {
                  fontSize: '16px',
                  color: '#424770',
                  '::placeholder': {
                    color: '#aab7c4',
                  },
                },
                invalid: {
                  color: '#9e2146',
                },
              },
              hidePostalCode: true,
            }}
          />
        </div>
      </div>

      {/* Security Note */}
      <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
        <div className="flex items-center space-x-2 text-blue-800 mb-2">
          <Shield className="h-5 w-5" />
          <span className="font-medium text-sm">Pagamento 100% Seguro</span>
        </div>
        <p className="text-blue-700 text-xs">
          Seus dados s√£o protegidos pela criptografia SSL e processados pelo Stripe, 
          a plataforma de pagamentos mais segura do mundo.
        </p>
      </div>

      {/* Payment Button */}
      <Button
        type="submit"
        disabled={!stripe || isLoading}
        size="lg"
        variant="doce"
        className="w-full"
      >
        {isLoading ? (
