import { NextRequest, NextResponse } from 'next/server'
import { createRouteHandlerClient } from '@supabase/auth-helpers-nextjs'
import { cookies } from 'next/headers'

export async function POST(request: NextRequest) {
  try {
    const supabase = createRouteHandlerClient({ cookies })
    const body = await request.json()
    const { paymentIntentId, plan = 'pro' } = body

    // Verificar se o usu√°rio est√° autenticado
    const { data: { session }, error: sessionError } = await supabase.auth.getSession()

    if (sessionError || !session?.user) {
      return NextResponse.json(
        { error: 'Usu√°rio n√£o autenticado' },
        { status: 401 }
      )
    }

    const userId = session.user.id

    // Atualizar o plano do usu√°rio no banco de dados
    const { data: updatedProfile, error: updateError } = await supabase
      .from('profiles')
      .update({
        plano: plan,
        updated_at: new Date().toISOString()
      })
      .eq('id', userId)
      .select()
      .single()

    if (updateError) {
      console.error('Erro ao atualizar perfil:', updateError)
      return NextResponse.json(
        { error: 'Erro ao atualizar perfil do usu√°rio' },
        { status: 500 }
      )
    }

    // Opcional: Salvar informa√ß√µes do pagamento para hist√≥rico
    /*
    const { error: paymentError } = await supabase
      .from('payments')
      .insert({
        user_id: userId,
        payment_intent_id: paymentIntentId,
        amount: 2900, // R$ 29,00 em centavos
        currency: 'BRL',
        status: 'completed',
        plan: plan,
        method: 'stripe',
        created_at: new Date().toISOString()
      })

    if (paymentError) {
      console.error('Erro ao salvar pagamento:', paymentError)
      // N√£o falhar a requisi√ß√£o se n√£o conseguir salvar o hist√≥rico
    }
    */

    return NextResponse.json({
      success: true,
      profile: updatedProfile,
      message: 'Upgrade realizado com sucesso!'
    })

  } catch (error: any) {
    console.error('Erro ao fazer upgrade do usu√°rio:', error)
    return NextResponse.json(
      { error: 'Erro interno do servidor' },
      { status: 500 }
    )
  }
}

