src/pages/UpgradeStripePage.tsx.content:
import { useState, useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { ArrowLeft, CreditCard, Shield, CheckCircle, Loader, User } from 'lucide-react'
import { useAuth } from '@/contexts/AuthContext'
import { planos } from '@/data/planos'
import { supabase } from '@/integrations/supabase/client'

export const UpgradeStripePage = () => {
  const { user, profile, upgradeUser } = useAuth()
  const navigate = useNavigate()
  const [loading, setLoading] = useState(false)
  const [checkingSubscription, setCheckingSubscription] = useState(false)

  const planoProfessional = planos.find(p => p.id === 'professional')!

  // Verificar subscription status ao carregar a p√°gina
  useEffect(() => {
    checkSubscriptionStatus()
  }, [])

  const checkSubscriptionStatus = async () => {
    if (!user) return

    setCheckingSubscription(true)

    try {
      console.log('Checking subscription status...')

      const { data, error } = await supabase.functions.invoke('check-subscription', {
        body: {
          userEmail: user.email
        }
      })

      if (error) {
        console.error('Error checking subscription:', error)
        return
      }

      console.log('Subscription status:', data)

      if (data.subscribed) {
        // Usu√°rio j√° tem assinatura ativa, atualizar estado e redirecionar
        if (upgradeUser) {
          upgradeUser('professional')
        }

        setTimeout(() => {
          navigate('/dashboard')
        }, 2000)
      }

    } catch (error) {
      console.error('Error:', error)
    } finally {
      setCheckingSubscription(false)
    }
  }

  const handleStripeCheckout = async () => {
    if (!user) {
      alert('Voc√™ precisa estar logado para fazer upgrade')
      return
    }

    setLoading(true)

    try {
      console.log('Creating Stripe checkout session...')

      const { data, error } = await supabase.functions.invoke('create-checkout', {
        body: {
          userEmail: user.email,
          userName: profile?.nome
        }
      })

      if (error) {
        console.error('Error creating checkout:', error)
        alert('Erro ao criar sess√£o de pagamento: ' + (error.message || 'Erro desconhecido'))
        return
      }

      console.log('Checkout session created:', data)

      // Abrir Stripe Checkout em nova aba
      if (data.url) {
        window.open(data.url, '_blank')
      }

    } catch (error) {
      console.error('Error:', error)
      alert('Erro interno. Tente novamente em alguns segundos.')
    } finally {
      setLoading(false)
    }
  }

  if (checkingSubscription) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <Loader className="w-8 h-8 animate-spin mx-auto mb-4 text-purple-600" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">
            Verificando sua assinatura...
          </h2>
          <p className="text-gray-600">Aguarde um momento</p>
        </div>
      </div>
    )
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        <div className="mb-8 flex items-center space-x-4">
          <Link 
            to="/upgrade"
            className="p-2 hover:bg-gray-200 rounded-lg transition-colors"
          >
            <ArrowLeft className="h-6 w-6" />
          </Link>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">
              Pagamento com Cart√£o
            </h1>
            <p className="text-gray-600">
              Finalize sua assinatura do plano Professional
            </p>
