import { serve } from "https://deno.land/std@0.190.0/http/server.ts"
import Stripe from "https://esm.sh/stripe@18.5.0"
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.57.2"

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
}

const logStep = (step: string, details?: any) => {
  const detailsStr = details ? ` - ${JSON.stringify(details)}` : ''
  console.log(`[CREATE-CHECKOUT] ${step}${detailsStr}`)
}

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders })
  }

  try {
    logStep("Function started")

    const stripeKey = Deno.env.get("STRIPE_SECRET_KEY")
    if (!stripeKey) throw new Error("STRIPE_SECRET_KEY is not set")
    logStep("Stripe key verified")

    // Get user info from request body instead of JWT
    const requestBody = await req.json()
    const { userEmail, userName } = requestBody

    if (!userEmail) {
      logStep("ERROR: Missing user email")
      return new Response(JSON.stringify({ error: "Email do usu√°rio √© obrigat√≥rio" }), {
        status: 400,
        headers: { ...corsHeaders, "Content-Type": "application/json" }
      })
    }

    logStep("User info received", { email: userEmail, name: userName })

    const stripe = new Stripe(stripeKey, { apiVersion: "2025-08-27.basil" })

    // Check if customer exists
    const customers = await stripe.customers.list({ email: userEmail, limit: 1 })
    let customerId
    if (customers.data.length > 0) {
      customerId = customers.data[0].id
      logStep("Existing customer found", { customerId })
    } else {
      logStep("No existing customer found")
    }

    const origin = req.headers.get("origin") || "http://localhost:3000"

    // Create checkout session with Professional plan price
    const session = await stripe.checkout.sessions.create({
      customer: customerId,
      customer_email: customerId ? undefined : userEmail,
      line_items: [
        {
          price: "price_1S944mEkyU3B9iiUQfeEaMVL", // DoceCalc Professional price ID
          quantity: 1,
        },
      ],
      mode: "subscription",
      success_url: `${origin}/upgrade-success?session_id={CHECKOUT_SESSION_ID}`,
      cancel_url: `${origin}/upgrade`,
      metadata: {
        userEmail: userEmail,
        userName: userName || '',
        plan: 'professional'
      },
      subscription_data: {
        metadata: {
          userEmail: userEmail,
          userName: userName || '',
          plan: 'professional'
        }
      }
    })

    logStep("Checkout session created", { sessionId: session.id, url: session.url })

    return new Response(JSON.stringify({ url: session.url }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    })
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error)
    logStep("ERROR in create-checkout", { message: errorMessage })

    // Return user-friendly error messages
    let userMessage = "Erro interno do servidor"
    if (errorMessage.includes("price_")) {
      userMessage = "Erro na configura√ß√£o do plano. Entre em contato com o suporte."
    } else if (errorMessage.includes("customer")) {
      userMessage = "Erro ao processar dados do cliente."
    } else if (errorMessage.includes("STRIPE_SECRET_KEY")) {
      userMessage = "Erro na configura√ß√£o de pagamento. Entre em contato com o suporte."
    }

    return new Response(JSON.stringify({ 
      error: userMessage,
      details: errorMessage 
    }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 500,
    })
  }
})

supabase/functions/create-pix/index.ts.content:
import { serve } from "https://deno.land/std@0.168.0/http/server.ts"
import { createClient } from 'https://esm.sh/@supabase/supabase-js@2'

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
}

const supabaseUrl = Deno.env.get('SUPABASE_URL')!
const supabaseServiceKey = Deno.env.get('SUPABASE_SERVICE_ROLE_KEY')!
const abacatePayToken = Deno.env.get('ABACATE_PAY_TOKEN')!

serve(async (req) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders })
  }

  try {
    console.log('Creating PIX payment with Abacate Pay...')

    const { amount, description, customer, metadata } = await req.json()

    // Validar campos obrigat√≥rios
    if (!customer || !customer.name || !customer.cellphone || !customer.email || !customer.taxId) {
      console.error('Missing required customer fields')
      return new Response(
        JSON.stringify({ error: 'Campos obrigat√≥rios: nome, telefone, email e CPF' }),
        { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      )
    }

    console.log('Calling Abacate Pay API...')

    const abacateResponse = await fetch('https://api.abacatepay.com/v1/pixQrCode/create', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${abacatePayToken}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        amount: Math.round(amount * 100), // Converter para centavos
        expiresIn: 15 * 60, // 15 minutos em segundos
        description: description || 'Upgrade para Plano Professional',
        customer: {
          name: customer.name,
          cellphone: customer.cellphone,
          email: customer.email,
          taxId: customer.taxId
        },
        metadata: metadata || {}
      }),
    })

    const responseData = await abacateResponse.json()
    console.log('Abacate Pay response:', responseData)

    if (!abacateResponse.ok) {
      console.error('Abacate Pay API error:', responseData)
      return new Response(
        JSON.stringify({ error: 'Erro ao criar pagamento PIX', details: responseData }),
        { status: abacateResponse.status, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
      )
    }

    if (responseData.error) {
      console.error('Abacate Pay returned error:', responseData.error)
    // Return user-friendly error messages
    let userMessage = "Erro ao criar pagamento PIX";
    if (responseData.error === "Invalid taxId") {
      userMessage = "CPF inv√°lido. Por favor, digite um CPF v√°lido no formato: 000.000.000-00";
    } else if (responseData.error === "Invalid cellphone") {
      userMessage = "Telefone inv√°lido. Use o formato: (11) 99999-9999";
    } else if (responseData.error === "Invalid email") {
      userMessage = "Email inv√°lido. Verifique o endere√ßo de email";
    } else if (responseData.error === "Invalid amount") {
      userMessage = "Valor do pagamento inv√°lido";
    }

    return new Response(
      JSON.stringify({ error: userMessage, details: responseData.error }),
      { status: 400, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    )
    }

    // Salvar informa√ß√µes do pagamento no banco
    const supabase = createClient(supabaseUrl, supabaseServiceKey)

    const { error: dbError } = await supabase
      .from('pagamentos')
      .insert({
        user_id: metadata?.userId || null,
        valor: amount,
        plano: 'professional',
        tipo_pagamento: 'pix',
        status: 'pendente',
        referencia_externa: responseData.data.id,
        dados_pagamento: responseData.data,
        data_vencimento: responseData.data.expiresAt,
        observacoes: 'Pagamento PIX via Abacate Pay'
      })

    if (dbError) {
      console.error('Error saving payment to database:', dbError)
      // N√£o falhar a requisi√ß√£o por causa do banco, mas logar o erro
    }

    console.log('PIX payment created successfully:', responseData.data.id)

    return new Response(
      JSON.stringify({ 
        success: true, 
        data: responseData.data 
      }),
      { headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    )

  } catch (error) {
    console.error('Error in create-pix function:', error)
    return new Response(
      JSON.stringify({ error: 'Erro interno do servidor', details: error.message }),
      { status: 500, headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    )
  }
})

supabase/functions/customer-portal/index.content:
import { serve } from "https://deno.land/std@0.190.0/http/server.ts"
import Stripe from "https://esm.sh/stripe@18.5.0"
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.57.2"

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
}

const logStep = (step: string, details?: any) => {
  const detailsStr = details ? ` - ${JSON.stringify(details)}` : ''
  console.log(`[CUSTOMER-PORTAL] ${step}${detailsStr}`)
}

serve(async (req) => {
  if (req.method === "OPTIONS") {
    return new Response(null, { headers: corsHeaders })
  }

  try {
    logStep("Function started")

    const stripeKey = Deno.env.get("STRIPE_SECRET_KEY")
    if (!stripeKey) throw new Error("STRIPE_SECRET_KEY is not set")
    logStep("Stripe key verified")

    const supabaseClient = createClient(
      Deno.env.get("SUPABASE_URL") ?? "",
      Deno.env.get("SUPABASE_SERVICE_ROLE_KEY") ?? "",
      { auth: { persistSession: false } }
    )

    const authHeader = req.headers.get("Authorization")
    if (!authHeader) throw new Error("No authorization header provided")
    logStep("Authorization header found")

    const token = authHeader.replace("Bearer ", "")
    const { data: userData, error: userError } = await supabaseClient.auth.getUser(token)
    if (userError) throw new Error(`Authentication error: ${userError.message}`)
    const user = userData.user
    if (!user?.email) throw new Error("User not authenticated or email not available")
    logStep("User authenticated", { userId: user.id, email: user.email })

    const stripe = new Stripe(stripeKey, { apiVersion: "2025-08-27.basil" })
    const customers = await stripe.customers.list({ email: user.email, limit: 1 })
    if (customers.data.length === 0) {
      throw new Error("No Stripe customer found for this user")
    }
    const customerId = customers.data[0].id
    logStep("Found Stripe customer", { customerId })

    const origin = req.headers.get("origin") || "https://7244efc1-8d1a-4aef-8e22-cdb49de4ce3d.lovableproject.com"
    const portalSession = await stripe.billingPortal.sessions.create({
      customer: customerId,
      return_url: `${origin}/dashboard`,
    })
    logStep("Customer portal session created", { sessionId: portalSession.id, url: portalSession.url })

    return new Response(JSON.stringify({ url: portalSession.url }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 200,
    })
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : String(error)
    logStep("ERROR in customer-portal", { message: errorMessage })
    return new Response(JSON.stringify({ error: errorMessage }), {
      headers: { ...corsHeaders, "Content-Type": "application/json" },
      status: 500,
    })
  }
})

