  },
  {
    id: 'profissional' as const,
    name: 'Profissional',
    price: 'R$ 47',
    period: '/m√™s',
    credits: '100 consultas',
    icon: <Crown className="h-6 w-6" />,
    color: 'blue',
    popular: true,
    features: [
      'Tudo do plano Gratuito',
      '100 consultas IA por m√™s',
      'Receitas ilimitadas',
      'An√°lise de cen√°rios avan√ßada',
      'Cat√°logo personalizado',
      'Relat√≥rios avan√ßados',
      'Integra√ß√£o com marketplace',
      'Suporte priorit√°rio',
    ],
  },
  {
    id: 'premium' as const,
    name: 'Premium',
    price: 'R$ 97',
    period: '/m√™s',
    credits: 'Ilimitado',
    icon: <Star className="h-6 w-6" />,
    color: 'purple',
    popular: false,
    features: [
      'Tudo do plano Profissional',
      'Consultas IA ilimitadas',
      'AI Assistant personalizado',
      'An√°lise de fotos de ingredientes',
      'Automa√ß√£o de processos',
      'API para integra√ß√µes',
      'Mentor exclusivo',
      'Suporte 24/7',
    ],
  },
]

export default function UpgradePlanModal({ 
  isOpen, 
  onClose, 
  onUpgrade, 
  currentPlan,
  trigger = 'manual'
}: UpgradePlanModalProps) {
  const [selectedPlan, setSelectedPlan] = useState<UserPlan['planType'] | null>(null)
  const [upgrading, setUpgrading] = useState(false)

  if (!isOpen) return null

  const getTitle = () => {
    switch (trigger) {
      case 'no_credits':
        return 'üö´ Ops! Cr√©ditos esgotados'
      case 'low_credits':
        return '‚ö†Ô∏è Poucos cr√©ditos restantes'
      default:
        return 'üöÄ Potencialize sua confeitaria'
    }
  }

  const getSubtitle = () => {
    switch (trigger) {
      case 'no_credits':
        return 'Voc√™ usou todos os seus cr√©ditos IA deste m√™s. Fa√ßa upgrade para continuar usando o DoceBot Pro!'
      case 'low_credits':
        return 'Seus cr√©ditos est√£o acabando. Que tal garantir mais consultas com a IA?'
      default:
        return 'Escolha o plano ideal para levar sua confeitaria ao pr√≥ximo n√≠vel com recursos avan√ßados'
    }
  }

  const handleUpgrade = async (planType: UserPlan['planType']) => {
    if (planType === currentPlan) return

    setUpgrading(true)
    setSelectedPlan(planType)

    // Simular processamento
    await new Promise(resolve => setTimeout(resolve, 2000))

    onUpgrade(planType)
    setUpgrading(false)
    onClose()
  }

  const getPlanButtonText = (planId: UserPlan['planType']) => {
    if (planId === currentPlan) return 'Plano Atual'
    if (upgrading && selectedPlan === planId) return 'Processando...'
    if (planId === 'gratuito') return 'Manter Gratuito'
    return 'Escolher Plano'
  }

  const isPlanDisabled = (planId: UserPlan['planType']) => {
    return planId === currentPlan || upgrading
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-2xl shadow-2xl max-w-5xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">{getTitle()}</h2>
            <p className="text-gray-600 mt-1">{getSubtitle()}</p>
          </div>
          <button
            onClick={onClose}
            className="p-2 hover:bg-gray-100 rounded-full transition-colors"
            disabled={upgrading}
          >
            <X className="h-6 w-6 text-gray-500" />
          </button>
        </div>

        {/* Plans Grid */}
        <div className="p-6 grid grid-cols-1 md:grid-cols-3 gap-6">
          {plans.map((plan) => {
            const isCurrentPlan = plan.id === currentPlan
            const isSelected = selectedPlan === plan.id
            const isPopular = plan.popular
            const isProcessing = upgrading && isSelected

            return (
              <div
                key={plan.id}
                className={`relative border-2 rounded-xl p-6 transition-all duration-200 ${
                  isCurrentPlan
                    ? 'border-green-500 bg-green-50'
                    : isPopular
                    ? 'border-blue-500 bg-blue-50'
                    : 'border-gray-200 hover:border-gray-300'
                }`}
              >
                {/* Popular Badge */}
                {isPopular && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <span className="bg-blue-500 text-white px-3 py-1 rounded-full text-xs font-medium flex items-center space-x-1">
                      <Sparkles className="h-3 w-3" />
                      <span>Mais Popular</span>
                    </span>
                  </div>
                )}

                {/* Current Plan Badge */}
                {isCurrentPlan && (
                  <div className="absolute -top-3 right-4">
                    <span className="bg-green-500 text-white px-3 py-1 rounded-full text-xs font-medium">
                      Atual
                    </span>
                  </div>
                )}

                {/* Plan Icon */}
                <div className={`flex items-center justify-center w-12 h-12 rounded-full mb-4 ${
                  plan.color === 'blue' ? 'bg-blue-100 text-blue-600' :
                  plan.color === 'purple' ? 'bg-purple-100 text-purple-600' :
                  'bg-gray-100 text-gray-600'
                }`}>
                  {plan.icon}
                </div>

                {/* Plan Details */}
                <h3 className="text-xl font-bold text-gray-900 mb-2">{plan.name}</h3>
                <div className="flex items-baseline mb-1">
                  <span className="text-3xl font-bold text-gray-900">{plan.price}</span>
                  <span className="text-gray-600 ml-1">{plan.period}</span>
                </div>
                <p className="text-sm text-gray-600 mb-4">{plan.credits} de IA</p>

                {/* Features */}
                <ul className="space-y-2 mb-6">
                  {plan.features.map((feature, index) => (
                    <li key={index} className="flex items-start space-x-2">
                      <Check className="h-4 w-4 text-green-500 mt-0.5 flex-shrink-0" />
                      <span className="text-sm text-gray-700">{feature}</span>
                    </li>
                  ))}
                </ul>

                {/* Action Button */}
                <button
                  onClick={() => handleUpgrade(plan.id)}
                  disabled={isPlanDisabled(plan.id)}
                  className={`w-full py-3 px-4 rounded-lg font-medium transition-all duration-200 ${
                    isCurrentPlan
                      ? 'bg-green-100 text-green-700 cursor-not-allowed'
                      : plan.color === 'blue'
                      ? 'bg-blue-600 hover:bg-blue-700 text-white'
                      : plan.color === 'purple'
                      ? 'bg-purple-600 hover:bg-purple-700 text-white'
                      : 'bg-gray-600 hover:bg-gray-700 text-white'
                  } ${
                    isPlanDisabled(plan.id) ? 'opacity-50 cursor-not-allowed' : ''
                  }`}
                >
                  {isProcessing && <Rocket className="h-4 w-4 mr-2 inline animate-spin" />}
                  {getPlanButtonText(plan.id)}
                </button>
              </div>
            )
          })}
        </div>

        {/* Footer */}
        <div className="p-6 border-t bg-gray-50">
          <div className="text-center text-sm text-gray-600">
            <p>‚ú® Todos os planos incluem:</p>
            <p className="mt-1">Acesso total √†s receitas ‚Ä¢ Suporte t√©cnico ‚Ä¢ Atualiza√ß√µes gratuitas</p>
            <p className="mt-2 text-xs">üí° Cancelamento a qualquer momento ‚Ä¢ Sem fidelidade</p>
          </div>
        </div>
      </div>
    </div>
  )
}

src/hooks/useChat.ts.content:
import { useState, useEffect } from 'react'
import { ChatMessage, ChatSession } from '../types/chat'
import { findAnswer } from '../utils/aiSearch'
import { personalizeAIResponse, getUserContext } from '../utils/aiPersonalization'
import { useCredits } from './useCredits'

interface UseChatReturn {
  messages: ChatMessage[]
  isLoading: boolean
  sendMessage: (content: string) => Promise<void>
  clearChat: () => void
  hasCredits: boolean
}

const STORAGE_KEY = 'doce_calc_chat_session'

export function useChat(): UseChatReturn {
  const [messages, setMessages] = useState<ChatMessage[]>([])
  const [isLoading, setIsLoading] = useState(false)
  const { canUseIA, useCredit } = useCredits()

  // Carregar mensagens salvas
  useEffect(() => {
    const savedSession = localStorage.getItem(STORAGE_KEY)
    if (savedSession) {
      try {
        const session: ChatSession = JSON.parse(savedSession)
        setMessages(session.messages)
      } catch (error) {
        console.error('Erro ao carregar chat:', error)
      }
    }
  }, [])

  // Salvar mensagens
  const saveSession = (newMessages: ChatMessage[]) => {
    const session: ChatSession = {
      id: 'current',
      messages: newMessages,
      createdAt: new Date(),
      updatedAt: new Date()
    }
    localStorage.setItem(STORAGE_KEY, JSON.stringify(session))
  }

  // Enviar mensagem
  const sendMessage = async (content: string): Promise<void> => {
    if (!content.trim()) return

    // Verificar cr√©ditos
    if (!canUseIA()) {
      return // O componente deve mostrar modal de upgrade
    }

    // Criar mensagem do usu√°rio
    const userMessage: ChatMessage = {
      id: `user_${Date.now()}`,
      sender: 'user',
      content: content.trim(),
      timestamp: new Date()
    }

    // Criar mensagem de loading da IA
    const loadingMessage: ChatMessage = {
      id: `ai_${Date.now()}`,
      sender: 'ai',
      content: '',
      timestamp: new Date(),
      isLoading: true
    }

    const newMessages = [...messages, userMessage, loadingMessage]
    setMessages(newMessages)
    setIsLoading(true)

    try {
      // Simular delay da IA (1-3 segundos)
      const delay = Math.random() * 2000 + 1000
      await new Promise(resolve => setTimeout(resolve, delay))

      // Buscar resposta na base de conhecimento
      const searchResult = findAnswer(content)

      // Usar cr√©dito
      const creditUsed = useCredit()
      if (!creditUsed) {
        throw new Error('Falha ao processar cr√©dito')
      }

      // Obter contexto do usu√°rio para personaliza√ß√£o
      const userContext = getUserContext()

      // Personalizar resposta da IA
      const aiResponse = personalizeAIResponse(searchResult, content, userContext)
      const confidence = searchResult.confidence
      const relatedQuestions = searchResult.relatedQuestions

      const aiMessage: ChatMessage = {
        id: `ai_${Date.now()}`,
        sender: 'ai',
        content: aiResponse,
        timestamp: new Date(),
        confidence,
        relatedQuestions
      }

      // Atualizar mensagens removendo loading e adicionando resposta
      const finalMessages = [...messages, userMessage, aiMessage]
      setMessages(finalMessages)
      saveSession(finalMessages)

    } catch (error) {
      console.error('Erro ao processar mensagem:', error)

      const errorMessage: ChatMessage = {
        id: `ai_${Date.now()}`,
        sender: 'ai',
        content: 'Ops! Algo deu errado. Tente novamente em alguns instantes. üòÖ',
        timestamp: new Date()
      }

      const errorMessages = [...messages, userMessage, errorMessage]
      setMessages(errorMessages)
      saveSession(errorMessages)
    } finally {
      setIsLoading(false)
    }
  }

  // Limpar chat
  const clearChat = () => {
    setMessages([])
    localStorage.removeItem(STORAGE_KEY)
  }

  return {
    messages,
    isLoading,
    sendMessage,
    clearChat,
    hasCredits: canUseIA()
  }
}

