import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { ArrowLeft, Search, ShoppingCart, Star, Clock, Crown, Filter, BookOpen, Download } from 'lucide-react'
import { ebooks, categories, getCart, saveCart, getUserLibrary, simulatePurchase } from '../data/ebooks-mock'
import { EBook, CartItem } from '../types/ebooks'
import { useCredits } from '../hooks/useCredits'

export default function MarketplacePage() {
  const [selectedCategory, setSelectedCategory] = useState<string>('all')
  const [searchTerm, setSearchTerm] = useState('')
  const [sortBy, setSortBy] = useState<'relevancia' | 'preco-menor' | 'preco-maior' | 'avaliacoes'>('relevancia')
  const [cart, setCart] = useState<CartItem[]>([])
  const [library, setLibrary] = useState<any>(null)
  const [showCart, setShowCart] = useState(false)
  const [purchasing, setPurchasing] = useState(false)
  const { remainingCredits } = useCredits()

  useEffect(() => {
    setCart(getCart())
    setLibrary(getUserLibrary())
  }, [])

  const filteredEbooks = ebooks
    .filter(ebook => {
      const matchesCategory = selectedCategory === 'all' || ebook.categoria === selectedCategory
      const matchesSearch = ebook.titulo.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           ebook.autor.toLowerCase().includes(searchTerm.toLowerCase()) ||
                           ebook.tags.some(tag => tag.toLowerCase().includes(searchTerm.toLowerCase()))
      return matchesCategory && matchesSearch
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'preco-menor':
          return a.preco - b.preco
        case 'preco-maior':
          return b.preco - a.preco
        case 'avaliacoes':
          return b.avaliacoes - a.avaliacoes
        default:
          return b.bestseller ? 1 : -1
      }
    })

  const addToCart = (ebook: EBook) => {
    const existingItem = cart.find(item => item.ebookId === ebook.id)
    let newCart

    if (existingItem) {
      newCart = cart.map(item =>
        item.ebookId === ebook.id
          ? { ...item, quantidade: item.quantidade + 1 }
          : item
      )
    } else {
      newCart = [...cart, { ebookId: ebook.id, ebook, quantidade: 1 }]
    }

    setCart(newCart)
    saveCart(newCart)
  }

  const removeFromCart = (ebookId: string) => {
    const newCart = cart.filter(item => item.ebookId !== ebookId)
    setCart(newCart)
    saveCart(newCart)
  }

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.ebook.preco * item.quantidade), 0)
  }

  const getTotalCredits = () => {
    return cart.reduce((total, item) => total + (item.ebook.precoCreditos * item.quantidade), 0)
  }

  const handlePurchase = async (paymentMethod: 'creditos' | 'dinheiro') => {
    if (cart.length === 0) return

    setPurchasing(true)
    try {
      const ebookIds = cart.map(item => item.ebookId)
      const result = await simulatePurchase(ebookIds, paymentMethod)

      if (result.success) {
        setCart([])
        setShowCart(false)
        setLibrary(getUserLibrary())
        alert('Compra realizada com sucesso! Os e-books foram adicionados √† sua biblioteca.')
      }
    } catch (error) {
      alert('Erro ao processar compra. Tente novamente.')
    } finally {
      setPurchasing(false)
    }
  }

  const isInLibrary = (ebookId: string) => {
    return library?.ebooks?.some((item: any) => item.ebookId === ebookId)
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <Link to="/dashboard" className="flex items-center text-gray-600 hover:text-gray-900">
                <ArrowLeft className="h-5 w-5 mr-2" />
                Voltar
              </Link>
              <h1 className="text-2xl font-bold text-gray-900">Marketplace de E-books</h1>
            </div>
            <div className="flex items-center space-x-4">
              <span className="text-sm text-gray-600">Cr√©ditos: {remainingCredits}</span>
              <button
                onClick={() => setShowCart(true)}
                className="relative p-2 text-gray-600 hover:text-gray-900"
              >
                <ShoppingCart className="h-6 w-6" />
                {cart.length > 0 && (
                  <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                    {cart.length}
                  </span>
                )}
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Sidebar com filtros */}
          <div className="lg:w-1/4">
            <div className="bg-white rounded-lg p-6 shadow-sm">
              <h3 className="font-semibold text-gray-900 mb-4">Filtros</h3>

              {/* Busca */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Buscar</label>
                <div className="relative">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="T√≠tulo, autor ou tag..."
                    className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                  />
                </div>
              </div>

              {/* Categorias */}
              <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">Categorias</label>
                <div className="space-y-2">
                  <button
                    onClick={() => setSelectedCategory('all')}
                    className={`w-full text-left px-3 py-2 rounded-lg ${
                      selectedCategory === 'all' ? 'bg-orange-100 text-orange-700' : 'text-gray-600 hover:bg-gray-100'
                    }`}
                  >
                    Todas as categorias
                  </button>
                  {categories.map(category => (
                    <button
                      key={category.id}
                      onClick={() => setSelectedCategory(category.id)}
                      className={`w-full text-left px-3 py-2 rounded-lg flex items-center ${
                        selectedCategory === category.id ? 'bg-orange-100 text-orange-700' : 'text-gray-600 hover:bg-gray-100'
                      }`}
                    >
                      <span className="mr-2">{category.icone}</span>
                      {category.nome} ({category.totalEbooks})
                    </button>
                  ))}
                </div>
              </div>

              {/* Ordena√ß√£o */}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">Ordenar por</label>
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value as any)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500"
                >
                  <option value="relevancia">Relev√¢ncia</option>
                  <option value="preco-menor">Menor pre√ßo</option>
                  <option value="preco-maior">Maior pre√ßo</option>
                  <option value="avaliacoes">Melhor avaliado</option>
                </select>
              </div>
            </div>
          </div>

          {/* Lista de e-books */}
          <div className="lg:w-3/4">
            <div className="mb-6">
              <p className="text-gray-600">
                {filteredEbooks.length} e-book(s) encontrado(s)
              </p>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
              {filteredEbooks.map(ebook => (
                <div key={ebook.id} className="bg-white rounded-lg shadow-sm border hover:shadow-md transition-shadow">
                  <div className="p-6">
                    <div className="flex items-start justify-between mb-4">
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          {ebook.bestseller && (
                            <Crown className="h-4 w-4 text-yellow-500" />
                          )}
                          <span className="text-xs bg-gray-100 px-2 py-1 rounded-full">
                            {categories.find(c => c.id === ebook.categoria)?.nome}
                          </span>
                        </div>
                        <h3 className="font-semibold text-gray-900 mb-1">{ebook.titulo}</h3>
                        <p className="text-sm text-gray-600 mb-2">por {ebook.autor}</p>
                      </div>
                    </div>

                    <p className="text-sm text-gray-600 mb-4 line-clamp-2">{ebook.resumo}</p>

                    <div className="flex items-center gap-4 mb-4">
                      <div className="flex items-center">
                        <Star className="h-4 w-4 text-yellow-400 fill-current" />
                        <span className="text-sm text-gray-600 ml-1">
                          {ebook.avaliacoes} ({ebook.numeroAvaliacoes})
                        </span>
                      </div>
                      <div className="flex items-center text-sm text-gray-600">
                        <BookOpen className="h-4 w-4 mr-1" />
                        {ebook.paginas}p
                      </div>
                    </div>

                    <div className="border-t pt-4">
                      <div className="flex items-center justify-between mb-4">
                        <div>
                          {ebook.promocao ? (
                            <div>
                              <span className="text-lg font-bold text-orange-600">
                                R$ {ebook.preco.toFixed(2)}
                              </span>
                              <span className="text-sm text-gray-500 line-through ml-2">
                                R$ {ebook.promocao.precoOriginal.toFixed(2)}
                              </span>
                            </div>
                          ) : (
                            <span className="text-lg font-bold text-gray-900">
                              R$ {ebook.preco.toFixed(2)}
                            </span>
                          )}
                          <div className="text-sm text-orange-600">
                            ou {ebook.precoCreditos} cr√©ditos
                          </div>
                        </div>
                      </div>

                      {isInLibrary(ebook.id) ? (
                        <div className="flex items-center justify-center py-2 bg-green-100 text-green-700 rounded-lg">
                          <Download className="h-4 w-4 mr-2" />
                          Na sua biblioteca
                        </div>
                      ) : (
                        <button
                          onClick={() => addToCart(ebook)}
                          className="w-full bg-orange-600 text-white py-2 px-4 rounded-lg hover:bg-orange-700 transition-colors flex items-center justify-center"
                        >
                          <ShoppingCart className="h-4 w-4 mr-2" />
                          Adicionar ao carrinho
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Modal do carrinho */}
      {showCart && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold">Carrinho de Compras</h3>
              <button
                onClick={() => setShowCart(false)}
                className="text-gray-400 hover:text-gray-600"
              >
                √ó
              </button>
            </div>

            {cart.length === 0 ? (
              <p className="text-gray-600">Seu carrinho est√° vazio</p>
            ) : (
              <>
                <div className="space-y-3 mb-4">
                  {cart.map(item => (
                    <div key={item.ebookId} className="flex items-center justify-between">
                      <div className="flex-1">
                        <h4 className="font-medium text-sm">{item.ebook.titulo}</h4>
                        <p className="text-sm text-gray-600">R$ {item.ebook.preco.toFixed(2)}</p>
                      </div>
                      <button
                        onClick={() => removeFromCart(item.ebookId)}
                        className="text-red-500 hover:text-red-700 ml-2"
                      >
                        Remover
                      </button>
                    </div>
                  ))}
                </div>

                <div className="border-t pt-4">
                  <div className="flex justify-between mb-4">
                    <span className="font-semibold">Total:</span>
                    <div className="text-right">
                      <div className="font-semibold">R$ {getTotalPrice().toFixed(2)}</div>
                      <div className="text-sm text-orange-600">{getTotalCredits()} cr√©ditos</div>
                    </div>
                  </div>

                  <div className="space-y-2">
                    <button
                      onClick={() => handlePurchase('dinheiro')}
                      disabled={purchasing}
                      className="w-full bg-green-600 text-white py-2 px-4 rounded-lg hover:bg-green-700 disabled:opacity-50"
                    >
                      {purchasing ? 'Processando...' : 'Comprar com Dinheiro'}
                    </button>
                    <button
                      onClick={() => handlePurchase('creditos')}
                      disabled={purchasing || remainingCredits < getTotalCredits()}
                      className="w-full bg-orange-600 text-white py-2 px-4 rounded-lg hover:bg-orange-700 disabled:opacity-50"
                    >
                      {purchasing ? 'Processando...' : 'Comprar com Cr√©ditos'}
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  )
}

src/utils/aiPersonalization.ts.content:
// Sistema de personaliza√ß√£o para DoceBot Pro

import { KnowledgeItem } from '../data/knowledge-base'
import { SearchResult } from './aiSearch'

export interface UserContext {
  nome?: string
  nivel?: 'iniciante' | 'intermediario' | 'avancado'
  especialidades?: string[]
  receitas?: string[]
  preferencias?: string[]
}

// Personalizar resposta da IA baseada no contexto do usu√°rio
export function personalizeAIResponse(
  searchResult: SearchResult,
  userQuestion: string,
  userContext: UserContext = {}
): string {
  const { nome = 'Confeiteiro(a)', nivel = 'iniciante', especialidades = [], receitas = [] } = userContext

  if (!searchResult.answer) {
    return generatePersonalizedFallback(nome, nivel, especialidades)
  }

  const baseResponse = searchResult.answer.resposta
  const personalizedIntro = generatePersonalizedIntro(nome, nivel, userQuestion)
  const adaptedResponse = adaptResponseToLevel(baseResponse, nivel)
  const personalizedTips = generatePersonalizedTips(searchResult.answer, receitas, especialidades)
  const confidenceNote = `\n\nüí° *Esta resposta tem ${searchResult.confidence}% de precis√£o baseada na sua pergunta.*`

  return `${personalizedIntro}\n\n${adaptedResponse}${personalizedTips}${confidenceNote}`
}

// Gerar introdu√ß√£o personalizada
function generatePersonalizedIntro(nome: string, nivel: string, question: string): string {
  const greetings = [
    `Ol√° ${nome}! Que √≥tima pergunta!`,
    `Oi ${nome}! Vou te ajudar com isso.`,
    `${nome}, essa √© uma excelente d√∫vida!`,
    `Perfeito, ${nome}! Vamos esclarecer isso.`
  ]

  const levelComments = {
    iniciante: 'Como voc√™ est√° come√ßando na confeitaria, vou explicar de forma bem did√°tica:',
    intermediario: 'Com sua experi√™ncia, voc√™ certamente vai aproveitar essas dicas:',
    avancado: 'Para algu√©m do seu n√≠vel, aqui est√£o os detalhes t√©cnicos:'
  }

  const greeting = greetings[Math.floor(Math.random() * greetings.length)]
  const levelComment = levelComments[nivel as keyof typeof levelComments] || levelComments.iniciante

  return `${greeting} ${levelComment}`
}

// Adaptar resposta ao n√≠vel do usu√°rio
function adaptResponseToLevel(response: string, nivel: string): string {
  switch (nivel) {
    case 'iniciante':
      return response + '\n\nüìö *Dica para iniciantes: Pratique bastante e n√£o tenha medo de errar. Cada erro √© um aprendizado!*'

    case 'intermediario':
      return response + '\n\n‚ö° *Dica pro: Experimente variar ingredientes e t√©cnicas para criar sua assinatura √∫nica.*'

    case 'avancado':
      return response + '\n\nüéØ *Para experts: Considere os aspectos cient√≠ficos como pH, temperatura e rea√ß√µes qu√≠micas para resultados perfeitos.*'

    default:
      return response
  }
}

// Gerar dicas personalizadas baseadas no perfil
function generatePersonalizedTips(
  answer: KnowledgeItem,
  userRecipes: string[],
  specialties: string[]
): string {
  const tips: string[] = []

  // Dicas baseadas nas especialidades do usu√°rio
  if (specialties.length > 0) {
    const relevantSpecialty = specialties.find(s => 
      answer.tags.some(tag => tag.includes(s.toLowerCase())) ||
      answer.palavrasChave.some(keyword => keyword.includes(s.toLowerCase()))
    )

    if (relevantSpecialty) {
      tips.push(`\nüî• *Conectando com sua especialidade em ${relevantSpecialty}: Esta t√©cnica vai elevar ainda mais seus ${relevantSpecialty}!*`)
    }
  }

  // Dicas baseadas nas receitas do usu√°rio
  if (userRecipes.length > 0) {
    const relatedRecipe = userRecipes.find(recipe =>
      answer.tags.some(tag => recipe.toLowerCase().includes(tag)) ||
      answer.palavrasChave.some(keyword => recipe.toLowerCase().includes(keyword))
    )

    if (relatedRecipe) {
      tips.push(`\nüí° *Aplicando em suas receitas: Voc√™ pode usar essa dica na sua receita de ${relatedRecipe}.*`)
    }
  }

  // Dicas baseadas na categoria
  const categoryTips = {
    precificacao: '\nüí∞ *Lembre-se: Um pre√ßo justo valoriza seu trabalho e garante a sustentabilidade do neg√≥cio.*',
    tecnicas: '\nüë©‚Äçüç≥ *Pr√°tica leva √† perfei√ß√£o: Anote suas observa√ß√µes para aprimorar cada vez mais.*',
    ingredientes: '\nü•Ñ *Qualidade dos ingredientes √© investimento: Bons ingredientes fazem toda a diferen√ßa no resultado final.*',
    marketing: '\nüì± *Consist√™ncia √© chave: Mantenha sua presen√ßa online ativa e aut√™ntica.*'
  }

  const categoryTip = categoryTips[answer.categoria as keyof typeof categoryTips]
  if (categoryTip) {
    tips.push(categoryTip)
  }

  return tips.join('')
}

// Gerar resposta de fallback personalizada
function generatePersonalizedFallback(
  nome: string,
  nivel: string,
  especialidades: string[]
): string {
  const specialtyText = especialidades.length > 0 
    ? ` Vejo que voc√™ trabalha com ${especialidades.join(', ')}, que tal algumas dicas espec√≠ficas?`
    : ''

  const levelText = {
    iniciante: 'Como voc√™ est√° come√ßando, sugiro focar primeiro nos fundamentos.',
    intermediario: 'Com sua experi√™ncia, voc√™ pode explorar t√©cnicas mais avan√ßadas.',
    avancado: 'Para algu√©m do seu n√≠vel, que tal experimentar inova√ß√µes na confeitaria?'
  }

  return `Ol√° ${nome}! N√£o encontrei uma resposta espec√≠fica para sua pergunta, mas estou aqui para ajudar!${specialtyText}

${levelText[nivel as keyof typeof levelText] || levelText.iniciante}

üç∞ **Que tal perguntar sobre:**
‚Ä¢ Como calcular pre√ßos de doces
‚Ä¢ T√©cnicas de preparo e decora√ß√£o  
‚Ä¢ Dicas sobre ingredientes
‚Ä¢ Estrat√©gias de marketing para confeitaria

Estou aqui para te ajudar a crescer na confeitaria! Qual sua pr√≥xima d√∫vida?`
}

// Obter contexto do usu√°rio do localStorage (mock)
export function getUserContext(): UserContext {
  try {
    const stored = localStorage.getItem('doce_calc_user_context')
    if (stored) {
      return JSON.parse(stored)
    }
  } catch (error) {
    console.error('Erro ao carregar contexto do usu√°rio:', error)
  }

  // Contexto padr√£o simulado
  return {
    nome: 'Confeiteiro(a)',
    nivel: 'iniciante',
    especialidades: ['brigadeiros', 'bolos'],
    receitas: ['Brigadeiro Gourmet', 'Bolo de Chocolate', 'Torta de Lim√£o'],
    preferencias: ['doces cremosos', 'decora√ß√£o simples']
  }
}

// Salvar contexto do usu√°rio
export function saveUserContext(context: UserContext): void {
  try {
    localStorage.setItem('doce_calc_user_context', JSON.stringify(context))
  } catch (error) {
    console.error('Erro ao salvar contexto do usu√°rio:', error)
  }
}

// Gerar sugest√µes de perguntas baseadas no perfil
export function generatePersonalizedSuggestions(userContext: UserContext): string[] {
  const { nivel, especialidades, receitas } = userContext

  const suggestions: string[] = []

  // Sugest√µes baseadas no n√≠vel
  const levelSuggestions = {
    iniciante: [
      'Quais equipamentos b√°sicos preciso para come√ßar?',
      'Como calcular meu primeiro pre√ßo?',
      'Qual a diferen√ßa entre a√ß√∫car cristal e refinado?'
    ],
    intermediario: [
      'Como fazer brigadeiro gourmet que vende bem?',
      'Qual a melhor forma de precificar bolos decorados?',
      'Como expandir minha clientela?'
    ],
    avancado: [
      'T√©cnicas avan√ßadas de temperar chocolate',
      'Como otimizar custos sem perder qualidade?',
      'Estrat√©gias de marketing digital para confeitaria'
    ]
  }

  suggestions.push(...(levelSuggestions[nivel as keyof typeof levelSuggestions] || levelSuggestions.iniciante))

  // Sugest√µes baseadas em especialidades
  if (especialidades?.includes('brigadeiros')) {
    suggestions.push('Como fazer brigadeiro que n√£o gruda na m√£o?')
  }
  if (especialidades?.includes('bolos')) {
    suggestions.push('Por que meu bolo afunda no meio?')
  }

  return suggestions.slice(0, 5)
}

src/utils/aiSearch.ts.content:
import { getFullKnowledgeBase, KnowledgeItem } from '../data/knowledge-base'

export interface SearchResult {
  answer: KnowledgeItem | null
  confidence: number
  suggestions: KnowledgeItem[]
  relatedQuestions: string[]
}

export interface SearchMatch {
  item: KnowledgeItem
  score: number
  matchedWords: string[]
}

// Fun√ß√£o principal de busca inteligente
export function findAnswer(userQuestion: string): SearchResult {
  const knowledgeBase = getFullKnowledgeBase()
  const cleanQuestion = normalizeText(userQuestion)
  const questionWords = extractWords(cleanQuestion)

  // Buscar matches na base de conhecimento
  const matches = searchMatches(questionWords, knowledgeBase)

  // Filtrar matches com score m√≠nimo
  const validMatches = matches.filter(match => match.score >= 2)

  if (validMatches.length === 0) {
    return {
      answer: null,
      confidence: 0,
      suggestions: getRandomSuggestions(knowledgeBase, 3),
      relatedQuestions: getDefaultQuestions()
    }
  }

  // Pegar o melhor match
  const bestMatch = validMatches[0]
  const confidence = calculateConfidence(bestMatch.score, questionWords.length)

  // Gerar sugest√µes relacionadas
  const suggestions = getSimilarQuestions(bestMatch.item, knowledgeBase, 3)
  const relatedQuestions = getRelatedQuestions(bestMatch.item.categoria, knowledgeBase)

  return {
    answer: bestMatch.item,
    confidence,
    suggestions,
    relatedQuestions
  }
}

// Normalizar texto removendo acentos e caracteres especiais
function normalizeText(text: string): string {
  return text
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '') // Remove acentos
    .replace(/[^\w\s]/g, ' ') // Remove pontua√ß√£o
    .replace(/\s+/g, ' ') // M√∫ltiplos espa√ßos em um
    .trim()
}

// Extrair palavras relevantes (remove stop words)
function extractWords(text: string): string[] {
  const stopWords = [
    'o', 'a', 'os', 'as', 'um', 'uma', 'uns', 'umas',
    'de', 'do', 'da', 'dos', 'das', 'em', 'no', 'na', 'nos', 'nas',
    'para', 'por', 'com', 'sem', 'ate', 'sobre', 'entre',
    'e', 'ou', 'mas', 'pois', 'que', 'se', 'como',
    'eu', 'tu', 'ele', 'ela', 'nos', 'vos', 'eles', 'elas',
    'meu', 'minha', 'meus', 'minhas', 'seu', 'sua', 'seus', 'suas',
    'este', 'esta', 'estes', 'estas', 'esse', 'essa', 'esses', 'essas',
    'aquele', 'aquela', 'aqueles', 'aquelas', 'isso', 'isto', 'aquilo',
    'ser', 'estar', 'ter', 'haver', 'fazer', 'poder', 'querer',
    'muito', 'mais', 'menos', 'bem', 'mal', 'melhor', 'pior'
  ]

  return text
    .split(' ')
    .filter(word => word.length > 2 && !stopWords.includes(word))
}

// Buscar matches na base de conhecimento
function searchMatches(questionWords: string[], knowledgeBase: KnowledgeItem[]): SearchMatch[] {
  const matches: SearchMatch[] = []

  for (const item of knowledgeBase) {
    const score = calculateMatchScore(questionWords, item)
    const matchedWords = getMatchedWords(questionWords, item)

    if (score > 0) {
      matches.push({
        item,
        score,
        matchedWords
      })
    }
  }

  // Ordenar por score (maior para menor)
  return matches.sort((a, b) => b.score - a.score)
}

// Calcular score de match
function calculateMatchScore(questionWords: string[], item: KnowledgeItem): number {
  let score = 0

  // Combinar todos os textos do item para busca
  const searchTexts = [
    normalizeText(item.pergunta),
    normalizeText(item.resposta),
    ...item.tags.map(normalizeText),
    ...item.palavrasChave.map(normalizeText)
  ].join(' ')

  // Contar matches diretos
  for (const word of questionWords) {
    if (searchTexts.includes(word)) {
      score += 1
    }
  }

  // Bonus para matches no t√≠tulo da pergunta
  const normalizedQuestion = normalizeText(item.pergunta)
  for (const word of questionWords) {
    if (normalizedQuestion.includes(word)) {
      score += 2
    }
  }

  // Bonus para matches em tags
  for (const tag of item.tags) {
    for (const word of questionWords) {
      if (normalizeText(tag).includes(word)) {
        score += 1.5
      }
    }
  }

  return score
}

// Obter palavras que fizeram match
function getMatchedWords(questionWords: string[], item: KnowledgeItem): string[] {
  const matched: string[] = []
  const searchTexts = [
    normalizeText(item.pergunta),
    ...item.tags.map(normalizeText),
    ...item.palavrasChave.map(normalizeText)
  ].join(' ')

  for (const word of questionWords) {
    if (searchTexts.includes(word)) {
      matched.push(word)
    }
  }

  return matched
}

// Calcular confian√ßa baseada no score
function calculateConfidence(score: number, questionLength: number): number {
  const maxPossibleScore = questionLength * 3 // Assumindo bonus m√°ximo
  const confidence = Math.min((score / maxPossibleScore) * 100, 95)
  return Math.round(confidence)
}

// Obter sugest√µes aleat√≥rias
function getRandomSuggestions(knowledgeBase: KnowledgeItem[], count: number): KnowledgeItem[] {
  const shuffled = [...knowledgeBase].sort(() => 0.5 - Math.random())
  return shuffled.slice(0, count)
}

// Obter perguntas relacionadas por categoria
function getRelatedQuestions(categoria: string, knowledgeBase: KnowledgeItem[]): string[] {
  return knowledgeBase
    .filter(item => item.categoria === categoria)
    .slice(0, 5)
    .map(item => item.pergunta)
}

// Obter perguntas similares baseadas em tags
function getSimilarQuestions(referenceItem: KnowledgeItem, knowledgeBase: KnowledgeItem[], count: number): KnowledgeItem[] {
  const similar: { item: KnowledgeItem; similarity: number }[] = []

  for (const item of knowledgeBase) {
    if (item.id === referenceItem.id) continue

    // Calcular similaridade baseada em tags em comum
    const commonTags = item.tags.filter(tag => referenceItem.tags.includes(tag))
    const similarity = commonTags.length

    if (similarity > 0) {
      similar.push({ item, similarity })
    }
  }

  return similar
    .sort((a, b) => b.similarity - a.similarity)
    .slice(0, count)
    .map(s => s.item)
}

// Perguntas padr√£o quando n√£o h√° match
function getDefaultQuestions(): string[] {
  return [
    'Como calcular pre√ßo de brigadeiro?',
    'Por que massa de bolo murcha?',
    'Como conservar chocolate?',
    'Como fotografar doces?',
    'Qual margem ideal para doces?'
  ]
}

// Fun√ß√£o para busca por categoria
export function searchByCategory(categoria: KnowledgeItem['categoria']): KnowledgeItem[] {
  const knowledgeBase = getFullKnowledgeBase()
  return knowledgeBase.filter(item => item.categoria === categoria)
}

// Fun√ß√£o para busca por dificuldade
export function searchByDifficulty(dificuldade: KnowledgeItem['dificuldade']): KnowledgeItem[] {
  const knowledgeBase = getFullKnowledgeBase()
  return knowledgeBase.filter(item => item.dificuldade === dificuldade)
}

// Fun√ß√£o para obter perguntas mais populares (mock)
export function getPopularQuestions(): KnowledgeItem[] {
  const knowledgeBase = getFullKnowledgeBase()
  return knowledgeBase
    .slice(0, 10) // Primeiras 10 como "mais populares"
    .sort(() => 0.5 - Math.random()) // Misturar para simular popularidade
}

// Fun√ß√£o para validar qualidade da resposta
export function validateSearchResult(result: SearchResult): boolean {
  if (!result.answer) return false
  if (result.confidence < 30) return false
  return true
}

