/**
 * Utilit√°rios para lidar com campos num√©ricos
 * Resolve problemas com v√≠rgulas que n√£o viram ponto e zeros fixos
 */

/**
 * Normaliza entrada num√©rica: converte v√≠rgulas para pontos
 * @param value - valor de entrada (pode ser string)
 * @returns string normalizada com pontos
 */
export const normalizeNumericInput = (value: string): string => {
  if (!value || value === '') return ''
  return value.replace(',', '.')
}

/**
 * Converte entrada de texto para n√∫mero, permitindo campo vazio
 * @param value - valor do input
 * @returns n√∫mero ou null se campo vazio/inv√°lido
 */
export const parseNumericInput = (value: string): number | null => {
  if (!value || value.trim() === '') return null

  const normalized = normalizeNumericInput(value)
  const parsed = parseFloat(normalized)

  return isNaN(parsed) ? null : parsed
}

/**
 * Converte entrada de texto para n√∫mero inteiro, permitindo campo vazio
 * @param value - valor do input
 * @returns n√∫mero inteiro ou null se campo vazio/inv√°lido
 */
export const parseIntegerInput = (value: string): number | null => {
  if (!value || value.trim() === '') return null

  const normalized = normalizeNumericInput(value)
  const parsed = parseInt(normalized)

  return isNaN(parsed) ? null : parsed
}

/**
 * Handler para mudan√ßas em campos num√©ricos que permite campo vazio
 * @param setValue - fun√ß√£o para atualizar o estado
 * @param field - nome do campo a ser atualizado
 * @param allowEmpty - se deve permitir valor null (padr√£o: true)
 * @returns fun√ß√£o handler para o onChange
 */
export const createNumericChangeHandler = (
  setValue: (field: string, value: number | null) => void,
  field: string,
  allowEmpty: boolean = true
) => {
  return (e: React.ChangeEvent<HTMLInputElement>) => {
    const parsed = parseNumericInput(e.target.value)

    if (parsed === null && !allowEmpty) {
      setValue(field, 0)
    } else {
      setValue(field, parsed)
    }
  }
}

/**
 * Handler para mudan√ßas em campos de n√∫meros inteiros que permite campo vazio
 * @param setValue - fun√ß√£o para atualizar o estado
 * @param field - nome do campo a ser atualizado
 * @param allowEmpty - se deve permitir valor null (padr√£o: true)
 * @returns fun√ß√£o handler para o onChange
 */
export const createIntegerChangeHandler = (
  setValue: (field: string, value: number | null) => void,
  field: string,
  allowEmpty: boolean = true
) => {
  return (e: React.ChangeEvent<HTMLInputElement>) => {
    const parsed = parseIntegerInput(e.target.value)

    if (parsed === null && !allowEmpty) {
      setValue(field, 0)
    } else {
      setValue(field, parsed)
    }
  }
}

/**
 * Formata valor num√©rico para exibi√ß√£o no input
 * @param value - valor num√©rico ou null
 * @returns string formatada para o input
 */
export const formatForInput = (value: number | null): string => {
  if (value === null || value === undefined) return ''
  return value.toString()
}

/**
 * Valida entrada num√©rica aceitando v√≠rgulas e pontos
 * @param value - valor a ser validado
 * @returns true se for um n√∫mero v√°lido
 */
export const isValidNumericInput = (value: string): boolean => {
  if (!value || value.trim() === '') return true // campo vazio √© v√°lido

  const normalized = normalizeNumericInput(value)
  return /^\d+([.,]\d+)?$/.test(value) && !isNaN(parseFloat(normalized))
}

/**
 * Validator para FormValidator que aceita v√≠rgulas e permite campo vazio
 * @param value - valor a ser validado
 * @returns string de erro ou vazio se v√°lido
 */
export const numericValidator = (value: string): string => {
  if (!value || value.trim() === '') return '' // campo vazio √© v√°lido

  if (!isValidNumericInput(value)) {
    return 'Digite apenas n√∫meros (use v√≠rgula ou ponto para decimais)'
  }

  return ''
}

/**
 * Validator para n√∫meros positivos que aceita v√≠rgulas e permite campo vazio
 * @param value - valor a ser validado
 * @returns string de erro ou vazio se v√°lido
 */
export const positiveNumericValidator = (value: string): string => {
  if (!value || value.trim() === '') return '' // campo vazio √© v√°lido

  const basicValidation = numericValidator(value)
  if (basicValidation) return basicValidation

  const parsed = parseNumericInput(value)
  if (parsed !== null && parsed <= 0) {
    return 'Deve ser um n√∫mero positivo'
  }

  return ''
}

