/**
 * Hook para formata√ß√£o de valores monet√°rios em Real brasileiro
 * Resolve problemas com campos monet√°rios como formata√ß√£o R$, valida√ß√£o e convers√£o
 */

import { useCallback } from 'react'

export const useFormatCurrency = () => {
  /**
   * Formata valor num√©rico para formato de moeda brasileira
   * @param value - valor num√©rico
   * @returns string formatada (ex: "R$ 123,45")
   */
  const formatToCurrency = useCallback((value: number | null): string => {
    if (value === null || value === undefined || isNaN(value)) {
      return 'R$ 0,00'
    }

    return new Intl.NumberFormat('pt-BR', {
      style: 'currency',
      currency: 'BRL',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(value)
  }, [])

  /**
   * Converte string monet√°ria formatada para n√∫mero
   * @param currencyString - string formatada (ex: "R$ 123,45" ou "123,45")
   * @returns n√∫mero correspondente (ex: 123.45)
   */
  const parseCurrency = useCallback((currencyString: string): number => {
    if (!currencyString || currencyString.trim() === '') {
      return 0
    }

    // Remove s√≠mbolos de moeda, espa√ßos e pontos de milhares
    const cleanString = currencyString
      .replace(/R\$\s?/g, '')
      .replace(/\./g, '') // Remove pontos de milhares
      .replace(',', '.') // Converte v√≠rgula decimal para ponto
      .trim()

    const parsed = parseFloat(cleanString)
    return isNaN(parsed) ? 0 : parsed
  }, [])

  /**
   * Formata valor para exibi√ß√£o em input (apenas n√∫meros com v√≠rgula)
   * @param value - valor num√©rico
   * @returns string formatada para input (ex: "123,45")
   */
  const formatForInput = useCallback((value: number | null): string => {
    if (value === null || value === undefined || isNaN(value)) {
      return ''
    }

    return value.toLocaleString('pt-BR', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    })
  }, [])

  /**
   * Aplica m√°scara monet√°ria enquanto o usu√°rio digita
   * @param value - valor atual do input
   * @returns valor formatado com m√°scara
   */
  const applyCurrencyMask = useCallback((value: string): string => {
    // Remove tudo que n√£o √© n√∫mero
    const numbers = value.replace(/\D/g, '')

    if (!numbers) return ''

    // Converte para centavos e depois para reais
    const cents = parseInt(numbers)
    const reais = cents / 100

    return reais.toLocaleString('pt-BR', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    })
  }, [])

  /**
   * Valida se uma string representa um valor monet√°rio v√°lido
   * @param value - string a ser validada
   * @returns true se v√°lido, false caso contr√°rio
   */
  const isValidCurrency = useCallback((value: string): boolean => {
    if (!value || value.trim() === '') return true // campo vazio √© v√°lido

    // Permite formatos como: 123,45 | 1.234,56 | R$ 123,45
    const currencyRegex = /^(R\$\s?)?(\d{1,3}(\.\d{3})*|\d+)(,\d{0,2})?$/
    return currencyRegex.test(value.trim())
  }, [])

  /**
   * Validator para uso com formul√°rios
   * @param value - valor a ser validado
   * @returns string de erro ou vazio se v√°lido
   */
  const currencyValidator = useCallback((value: string): string => {
    if (!value || value.trim() === '') return '' // campo vazio √© v√°lido

    if (!isValidCurrency(value)) {
      return 'Digite um valor monet√°rio v√°lido (ex: 123,45)'
    }

    const numericValue = parseCurrency(value)
    if (numericValue < 0) {
      return 'Valor deve ser positivo'
    }

    return ''
  }, [isValidCurrency, parseCurrency])

  return {
    formatToCurrency,
    parseCurrency,
    formatForInput,
    applyCurrencyMask,
    isValidCurrency,
    currencyValidator,
  }
}

