app/globals.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Poppins:wght@400;500;600;700&display=swap');

@layer base {
  :root {
    --background: 0 0% 100%;
    --foreground: 222.2 84% 4.9%;
    --card: 0 0% 100%;
    --card-foreground: 222.2 84% 4.9%;
    --popover: 0 0% 100%;
    --popover-foreground: 222.2 84% 4.9%;
    --primary: 346 77% 49%;
    --primary-foreground: 355 100% 97%;
    --secondary: 210 40% 98%;
    --secondary-foreground: 222.2 84% 4.9%;
    --muted: 210 40% 98%;
    --muted-foreground: 215.4 16.3% 46.9%;
    --accent: 210 40% 98%;
    --accent-foreground: 222.2 84% 4.9%;
    --destructive: 0 84.2% 60.2%;
    --destructive-foreground: 210 40% 98%;
    --border: 214.3 31.8% 91.4%;
    --input: 214.3 31.8% 91.4%;
    --ring: 346 77% 49%;
    --radius: 0.5rem;
  }

  .dark {
    --background: 222.2 84% 4.9%;
    --foreground: 210 40% 98%;
    --card: 222.2 84% 4.9%;
    --card-foreground: 210 40% 98%;
    --popover: 222.2 84% 4.9%;
    --popover-foreground: 210 40% 98%;
    --primary: 346 77% 49%;
    --primary-foreground: 355 100% 97%;
    --secondary: 217.2 32.6% 17.5%;
    --secondary-foreground: 210 40% 98%;
    --muted: 217.2 32.6% 17.5%;
    --muted-foreground: 215 20.2% 65.1%;
    --accent: 217.2 32.6% 17.5%;
    --accent-foreground: 210 40% 98%;
    --destructive: 0 62.8% 30.6%;
    --destructive-foreground: 210 40% 98%;
    --border: 217.2 32.6% 17.5%;
    --input: 217.2 32.6% 17.5%;
    --ring: 346 77% 49%;
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans;
    font-feature-settings: "rlig" 1, "calt" 1;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-display;
  }
}

@layer components {
  /* Loading animations */
  .animate-pulse-soft {
    animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-fade-in {
    animation: fade-in 0.3s ease-out forwards;
  }

  /* Custom scrollbar */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    @apply bg-gray-100 rounded-full;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    @apply bg-gray-300 rounded-full hover:bg-gray-400;
  }

  /* Glassmorphism effect */
  .glass {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
  }

  /* Gradient backgrounds */
  .bg-gradient-doce {
    background: linear-gradient(135deg, #f59e32 0%, #ec4899 100%);
  }

  .bg-gradient-soft {
    background: linear-gradient(135deg, #fef7ed 0%, #fdf2f8 100%);
  }

  /* Button variants */
  .btn-doce {
    @apply bg-gradient-doce text-white font-medium px-6 py-2.5 rounded-lg;
    @apply transition-all duration-200 ease-in-out;
    @apply hover:shadow-lg hover:shadow-doce-500/25 hover:scale-105;
    @apply active:scale-95;
  }

  /* Card variants */
  .card-doce {
    @apply bg-white rounded-xl shadow-sm border border-gray-200/50;
    @apply hover:shadow-md transition-shadow duration-200;
  }

  .card-gradient {
    @apply bg-gradient-soft rounded-xl shadow-sm border border-gray-200/50;
  }

  /* Input focus states */
  .input-focus {
    @apply focus:ring-2 focus:ring-primary/20 focus:border-primary;
    @apply transition-all duration-200;
  }

  /* Status indicators */
  .status-success {
    @apply bg-green-100 text-green-800 border border-green-200;
  }

  .status-warning {
    @apply bg-yellow-100 text-yellow-800 border border-yellow-200;
  }

  .status-error {
    @apply bg-red-100 text-red-800 border border-red-200;
  }

  .status-info {
    @apply bg-blue-100 text-blue-800 border border-blue-200;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  /* Hide scrollbar but keep functionality */
  .scrollbar-hide {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .scrollbar-hide::-webkit-scrollbar {
    display: none;
  }

  /* Mobile-first responsive text */
  .text-responsive {
    @apply text-sm sm:text-base;
  }

  .heading-responsive {
    @apply text-xl sm:text-2xl lg:text-3xl;
  }

  /* Safe area for mobile */
  .safe-area-inset {
    padding-left: env(safe-area-inset-left);
    padding-right: env(safe-area-inset-right);
  }
}

/* Animations personalizadas */
@keyframes shimmer {
  0% { background-position: -200px 0; }
  100% { background-position: calc(200px + 100%) 0; }
}

.shimmer {
  background: linear-gradient(90deg, 
    transparent, 
    rgba(255, 255, 255, 0.4), 
    transparent
  );
  background-size: 200px 100%;
  animation: shimmer 1.5s infinite;
}

/* Loading skeleton */
.skeleton {
  @apply bg-gray-200 animate-pulse rounded;
}

.skeleton-text {
  @apply skeleton h-4 w-full mb-2;
}

.skeleton-title {
  @apply skeleton h-6 w-3/4 mb-4;
}

/* Print styles */
@media print {
  .no-print {
    display: none !important;
  }

  body {
    -webkit-print-color-adjust: exact;
    print-color-adjust: exact;
  }
}

