import { pgTable, serial, varchar, text, timestamp, boolean, numeric, integer } from 'drizzle-orm/pg-core'
import { relations } from 'drizzle-orm'

// Tabela de usu√°rios (profiles)
export const profiles = pgTable('profiles', {
  id: varchar('id').primaryKey(), // UUID
  email: varchar('email').notNull().unique(),
  nome: varchar('nome').notNull(),
  password: varchar('password').notNull(), // Hash da senha
  plano: varchar('plano').notNull().default('free'), // 'free' ou 'pro'
  taxa_horaria: numeric('taxa_horaria', { precision: 10, scale: 2 }).default('25.00'),
  margem_lucro: numeric('margem_lucro', { precision: 5, scale: 2 }).default('30.00'),
  created_at: timestamp('created_at').defaultNow(),
  updated_at: timestamp('updated_at').defaultNow(),
})

// Tabela de ingredientes
export const ingredientes = pgTable('ingredientes', {
  id: serial('id').primaryKey(),
  user_id: varchar('user_id').notNull().references(() => profiles.id, { onDelete: 'cascade' }),
  nome: varchar('nome').notNull(),
  categoria: varchar('categoria').notNull().default('outros'),
  preco: numeric('preco', { precision: 10, scale: 2 }).notNull(),
  unidade: varchar('unidade').notNull().default('unidade'),
  observacoes: text('observacoes'),
  created_at: timestamp('created_at').defaultNow(),
  updated_at: timestamp('updated_at').defaultNow(),
})

// Tabela de receitas
export const receitas = pgTable('receitas', {
  id: serial('id').primaryKey(),
  user_id: varchar('user_id').notNull().references(() => profiles.id, { onDelete: 'cascade' }),
  nome: varchar('nome').notNull(),
  descricao: text('descricao'),
  rendimento: integer('rendimento').notNull().default(1),
  tempo_preparo: integer('tempo_preparo').notNull().default(60), // em minutos
  complexidade: integer('complexidade').notNull().default(1), // 1-5
  categoria: varchar('categoria').notNull().default('doces'),
  created_at: timestamp('created_at').defaultNow(),
  updated_at: timestamp('updated_at').defaultNow(),
})

// Tabela de ingredientes das receitas
export const receita_ingredientes = pgTable('receita_ingredientes', {
  id: serial('id').primaryKey(),
  receita_id: integer('receita_id').notNull().references(() => receitas.id, { onDelete: 'cascade' }),
  ingrediente_id: integer('ingrediente_id').notNull().references(() => ingredientes.id, { onDelete: 'cascade' }),
  quantidade: numeric('quantidade', { precision: 10, scale: 3 }).notNull(),
  unidade: varchar('unidade').notNull(),
})

// Relations
export const profilesRelations = relations(profiles, ({ many }) => ({
  ingredientes: many(ingredientes),
  receitas: many(receitas),
}))

export const ingredientesRelations = relations(ingredientes, ({ one, many }) => ({
  user: one(profiles, {
    fields: [ingredientes.user_id],
    references: [profiles.id],
  }),
  receita_ingredientes: many(receita_ingredientes),
}))

export const receitasRelations = relations(receitas, ({ one, many }) => ({
  user: one(profiles, {
    fields: [receitas.user_id],
    references: [profiles.id],
  }),
  ingredientes: many(receita_ingredientes),
}))

export const receitaIngredientesRelations = relations(receita_ingredientes, ({ one }) => ({
  receita: one(receitas, {
    fields: [receita_ingredientes.receita_id],
    references: [receitas.id],
  }),
  ingrediente: one(ingredientes, {
    fields: [receita_ingredientes.ingrediente_id],
    references: [ingredientes.id],
  }),
}))

// Types
export type Profile = typeof profiles.$inferSelect
export type InsertProfile = typeof profiles.$inferInsert
export type Ingrediente = typeof ingredientes.$inferSelect
export type InsertIngrediente = typeof ingredientes.$inferInsert
export type Receita = typeof receitas.$inferSelect
export type InsertReceita = typeof receitas.$inferInsert

