                </CardContent>
              </Card>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {receitasFiltradas.map((receita) => (
                  <Card key={receita.id} className="hover:shadow-lg transition-shadow">
                    <CardHeader className="pb-3">
                      <div className="flex items-start justify-between">
                        <div className="flex-1">
                          <CardTitle className="text-lg mb-1">{receita.nome}</CardTitle>
                          <div className="flex items-center space-x-2 mb-2">
                            <Badge 
                              variant="secondary"
                              className={getComplexidadeColor(receita.complexidade)}
                            >
                              {getComplexidadeLabel(receita.complexidade)}
                            </Badge>
                            <Badge variant="outline" className="text-xs">
                              {receita.categoria}
                            </Badge>
                          </div>
                        </div>
                        {!receita.ativa && (
                          <Badge variant="outline" className="text-xs text-gray-500">
                            Inativa
                          </Badge>
                        )}
                      </div>

                      {receita.descricao && (
                        <CardDescription className="text-sm line-clamp-2">
                          {receita.descricao}
                        </CardDescription>
                      )}
                    </CardHeader>

                    <CardContent className="space-y-4">
                      <div className="grid grid-cols-2 gap-4 text-sm">
                        <div className="flex items-center space-x-2">
                          <Clock className="h-4 w-4 text-gray-400" />
                          <span>{receita.tempo_producao} min</span>
                        </div>
                        <div className="flex items-center space-x-2">
                          <Users className="h-4 w-4 text-gray-400" />
                          <span>{receita.rendimento} un</span>
                        </div>
                      </div>

                      {receita.custo_calculado && (
                        <div className="bg-green-50 p-3 rounded-lg">
                          <div className="flex justify-between items-center text-sm">
                            <span className="text-green-800 font-medium">Custo calculado:</span>
                            <span className="text-green-900 font-bold">
                              {formatarMoeda(receita.custo_calculado)}
                            </span>
                          </div>
                          {receita.preco_sugerido && (
                            <div className="flex justify-between items-center text-sm mt-1">
                              <span className="text-green-800 font-medium">Pre√ßo sugerido:</span>
                              <span className="text-green-900 font-bold">
                                {formatarMoeda(receita.preco_sugerido)}
                              </span>
                            </div>
                          )}
                        </div>
                      )}

                      <div className="flex flex-wrap gap-2">
                        <Button 
                          size="sm" 
                          variant="doce"
                          onClick={() => carregarDetalhesReceita(receita)}
                        >
                          <Eye className="h-4 w-4 mr-1" />
                          Ver Detalhes
                        </Button>
                        <Link href={`/dashboard/calculadora?receita=${receita.id}`}>
                          <Button size="sm" variant="outline">
                            <Calculator className="h-4 w-4 mr-1" />
                            Calcular
                          </Button>
                        </Link>
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => handleEditReceita(receita)}
                        >
                          <Edit className="h-4 w-4 mr-1" />
                          Editar
                        </Button>
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => handleDuplicateReceita(receita)}
                          disabled={!isPro && receitas.length >= 3}
                        >
                          <Copy className="h-4 w-4 mr-1" />
                          Duplicar
                        </Button>
                        <Button 
                          size="sm" 
                          variant="outline"
                          onClick={() => handleDeleteReceita(receita)}
                          className="text-red-600 hover:text-red-700"
                        >
                          <Trash2 className="h-4 w-4 mr-1" />
                          Excluir
                        </Button>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </>
        )}

        {/* Modal de Vista Detalhada */}
        {showDetalheModal && receitaDetalhada && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
              <div className="p-6 border-b">
                <div className="flex items-center justify-between">
                  <h2 className="text-2xl font-bold">{receitaDetalhada.nome}</h2>
                  <Button
                    onClick={() => {
                      setShowDetalheModal(false)
                      setReceitaDetalhada(null)
                      setIngredientesDetalhada([])
                      setCustoCalculadoDetalhado(null)
                    }}
                    variant="ghost"
                    size="sm"
                  >
                    <Minus className="h-5 w-5" />
                  </Button>
                </div>
              </div>

              <div className="p-6 space-y-6">
                {/* Informa√ß√µes b√°sicas */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <h3 className="font-semibold mb-3">Informa√ß√µes Gerais</h3>
                    <div className="space-y-2 text-sm">
                      <div className="flex justify-between">
                        <span className="text-gray-600">Categoria:</span>
                        <span className="font-medium">{receitaDetalhada.categoria}</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Complexidade:</span>
                        <Badge className={getComplexidadeColor(receitaDetalhada.complexidade)}>
                          {getComplexidadeLabel(receitaDetalhada.complexidade)}
                        </Badge>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Tempo de produ√ß√£o:</span>
                        <span className="font-medium">{receitaDetalhada.tempo_producao} minutos</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Rendimento:</span>
                        <span className="font-medium">{receitaDetalhada.rendimento} unidades</span>
                      </div>
                      <div className="flex justify-between">
                        <span className="text-gray-600">Status:</span>
                        <Badge variant={receitaDetalhada.ativa ? "default" : "outline"}>
                          {receitaDetalhada.ativa ? 'Ativa' : 'Inativa'}
                        </Badge>
                      </div>
                    </div>
                  </div>

                  <div>
                    <h3 className="font-semibold mb-3">Custos e Pre√ßos</h3>
                    <div className="space-y-2 text-sm">
                      {custoCalculadoDetalhado && (
                        <div className="flex justify-between">
                          <span className="text-gray-600">Custo calculado:</span>
                          <span className="font-bold text-green-600">
                            {formatarMoeda(custoCalculadoDetalhado)}
                          </span>
                        </div>
                      )}
                      {receitaDetalhada.custo_calculado && (
                        <div className="flex justify-between">
                          <span className="text-gray-600">Custo salvo:</span>
                          <span className="font-medium">
                            {formatarMoeda(receitaDetalhada.custo_calculado)}
                          </span>
                        </div>
                      )}
                      {receitaDetalhada.preco_sugerido && (
                        <div className="flex justify-between">
                          <span className="text-gray-600">Pre√ßo sugerido:</span>
                          <span className="font-medium">
                            {formatarMoeda(receitaDetalhada.preco_sugerido)}
                          </span>
                        </div>
                      )}
                      {custoCalculadoDetalhado && (
                        <div className="flex justify-between">
                          <span className="text-gray-600">Custo por unidade:</span>
                          <span className="font-medium">
                            {formatarMoeda(custoCalculadoDetalhado / receitaDetalhada.rendimento)}
                          </span>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Descri√ß√£o */}
                {receitaDetalhada.descricao && (
                  <div>
                    <h3 className="font-semibold mb-3">Descri√ß√£o</h3>
                    <p className="text-sm text-gray-700 bg-gray-50 p-3 rounded-lg">
                      {receitaDetalhada.descricao}
                    </p>
                  </div>
                )}

                {/* Ingredientes */}
                <div>
                  <h3 className="font-semibold mb-3 flex items-center">
                    <ShoppingCart className="h-5 w-5 mr-2" />
                    Ingredientes ({ingredientesDetalhada.length})
                  </h3>
                  {loadingIngredientes ? (
                    <div className="flex items-center justify-center py-8">
                      <div className="animate-spin w-6 h-6 border-2 border-primary border-t-transparent rounded-full" />
                    </div>
                  ) : ingredientesDetalhada.length === 0 ? (
                    <div className="text-center py-8 bg-gray-50 rounded-lg">
                      <Package className="h-12 w-12 text-gray-400 mx-auto mb-2" />
                      <p className="text-gray-500">Nenhum ingrediente cadastrado</p>
                      <p className="text-sm text-gray-400">Edite a receita para adicionar ingredientes</p>
                    </div>
                  ) : (
                    <div className="space-y-3">
                      {ingredientesDetalhada.map((ingredienteReceita) => {
                        const custoIngrediente = (ingredienteReceita.quantidade / 1000) * ingredienteReceita.ingrediente.preco_kg
                        return (
                          <div 
                            key={ingredienteReceita.ingrediente_id} 
                            className="flex items-center justify-between p-3 bg-gray-50 rounded-lg"
                          >
                            <div className="flex-1">
                              <div className="font-medium">{ingredienteReceita.ingrediente.nome}</div>
                              <div className="text-sm text-gray-500">
                                {formatarQuantidade(ingredienteReceita.quantidade, ingredienteReceita.ingrediente.unidade)} ¬∑ 
                                {formatarMoeda(ingredienteReceita.ingrediente.preco_kg)}/kg
                              </div>
                            </div>
                            <div className="text-right">
                              <div className="font-medium">{formatarMoeda(custoIngrediente)}</div>
                            </div>
                          </div>
                        )
                      })}

                      {custoCalculadoDetalhado && (
                        <div className="border-t pt-3 mt-4">
                          <div className="flex justify-between items-center font-semibold">
                            <span>Total dos ingredientes:</span>
                            <span className="text-lg">
                              {formatarMoeda(
                                ingredientesDetalhada.reduce((acc, ir) => 
                                  acc + ((ir.quantidade / 1000) * ir.ingrediente.preco_kg), 0
                                )
                              )}
                            </span>
                          </div>
                        </div>
                      )}
                    </div>
                  )}
                </div>

                {/* A√ß√µes */}
                <div className="flex gap-3 pt-4 border-t">
                  <Link href={`/dashboard/calculadora?receita=${receitaDetalhada.id}`}>
                    <Button variant="doce">
                      <Calculator className="h-4 w-4 mr-2" />
                      Calcular Pre√ßos
                    </Button>
                  </Link>
                  <Button 
                    variant="outline"
                    onClick={() => {
                      setShowDetalheModal(false)
                      handleEditReceita(receitaDetalhada)
                    }}
                  >
                    <Edit className="h-4 w-4 mr-2" />
                    Editar Receita
                  </Button>
                  <Button 
                    variant="outline"
                    onClick={() => handleDuplicateReceita(receitaDetalhada)}
                    disabled={!isPro && receitas.length >= 3}
                  >
                    <Copy className="h-4 w-4 mr-2" />
                    Duplicar
                  </Button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

app/dashboard/relatorios/page.tsx.content:
'use client'

import { useState, useEffect } from 'react'
import { useAuth } from '@/components/auth/auth-provider'
import { Button } from '@/components/ui/button'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { Input } from '@/components/ui/input'
import { Label } from '@/components/ui/label'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { 
  BarChart3,
  PieChart,
  TrendingUp,
  TrendingDown,
  DollarSign,
  FileText,
  Calendar,
  Download,
  Eye,
  Calculator,
  ChefHat,
  Package,
  Users,
  Target,
  Crown,
  CheckCircle,
  Clock,
  Filter,
  RefreshCw
} from 'lucide-react'
import { useToast } from '@/hooks/use-toast'
import { supabase } from '@/lib/supabase-client'
import { formatarMoeda } from '@/lib/calculations-lib'

type RelatorioFinanceiro = {
  periodo: string
  totalReceitas: number
  totalOrcamentos: number
  valorMedioOrcamento: number
  receitasCalculadas: number
  margemMediaLucro: number
  ingredientesCadastrados: number
  receitasMaisRentaveis: {
    nome: string
    margem: number
    custo: number
    preco: number
  }[]
  tendenciaVendas: {
    mes: string
    valor: number
  }[]
}

type EstatisticasGerais = {
  totalReceitas: number
  receitasAtivas: number
  totalIngredientes: number
  ingredientesAtivos: number
  totalOrcamentos: number
  orcamentosPendentes: number
  orcamentosAprovados: number
  valorTotalOrcamentos: number
  ultimosCalculos: {
    receita: string
    valor: number
    data: string
  }[]
}

type RelatorioIngredientes = {
  ingredientesMaisUsados: {
    nome: string
    categoria: string
    usos: number
    custoMedio: number
  }[]
  categoriasMaisUsadas: {
    nome: string
    quantidade: number
    cor: string
  }[]
  ingredientesCaros: {
    nome: string
    preco: number
    categoria: string
  }[]
}

export default function RelatoriosPage() {
  const { profile } = useAuth()
  const { toast } = useToast()

  // Estados principais
  const [loading, setLoading] = useState(true)
  const [activeTab, setActiveTab] = useState('geral')
  const [periodoFiltro, setPeriodoFiltro] = useState('30') // dias

  // Estados dos relat√≥rios
  const [estatisticasGerais, setEstatisticasGerais] = useState<EstatisticasGerais | null>(null)
  const [relatorioFinanceiro, setRelatorioFinanceiro] = useState<RelatorioFinanceiro | null>(null)
  const [relatorioIngredientes, setRelatorioIngredientes] = useState<RelatorioIngredientes | null>(null)

  const isPro = profile?.plano === 'pro'

  useEffect(() => {
    if (profile?.id && isPro) {
      carregarTodosRelatorios()
    }
  }, [profile?.id, isPro, periodoFiltro])

  const carregarTodosRelatorios = async () => {
    setLoading(true)
    try {
      await Promise.all([
        carregarEstatisticasGerais(),
        carregarRelatorioFinanceiro(),
        carregarRelatorioIngredientes()
      ])
    } catch (error) {
      console.error('Erro ao carregar relat√≥rios:', error)
      toast({
        title: "Erro ao carregar relat√≥rios",
        description: "N√£o foi poss√≠vel carregar os dados dos relat√≥rios.",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const carregarEstatisticasGerais = async () => {
    try {
      // Carregar receitas
      const { data: receitas } = await supabase
        .from('receitas')
        .select('id, nome, ativa, custo_calculado, preco_sugerido')
        .eq('user_id', profile?.id)

      // Carregar ingredientes
      const { data: ingredientes } = await supabase
        .from('ingredientes')
        .select('id, nome, ativo')
        .eq('user_id', profile?.id)

      // Carregar or√ßamentos
      const { data: orcamentos } = await supabase
        .from('orcamentos')
        .select('id, status, valor_total, created_at')
        .eq('user_id', profile?.id)

      // Carregar √∫ltimos c√°lculos
      const { data: calculos } = await supabase
        .from('calculos_salvos')
        .select(`
          id, preco_total, created_at,
          receitas (nome)
        `)
        .eq('user_id', profile?.id)
        .order('created_at', { ascending: false })
        .limit(5)

      const stats: EstatisticasGerais = {
        totalReceitas: receitas?.length || 0,
        receitasAtivas: receitas?.filter(r => r.ativa).length || 0,
        totalIngredientes: ingredientes?.length || 0,
        ingredientesAtivos: ingredientes?.filter(i => i.ativo).length || 0,
        totalOrcamentos: orcamentos?.length || 0,
        orcamentosPendentes: orcamentos?.filter(o => o.status === 'pendente').length || 0,
        orcamentosAprovados: orcamentos?.filter(o => o.status === 'aprovado').length || 0,
        valorTotalOrcamentos: orcamentos?.reduce((sum, o) => sum + o.valor_total, 0) || 0,
        ultimosCalculos: calculos?.map(c => ({
          receita: (c as any).receitas?.nome || 'Receita removida',
          valor: c.preco_total,
          data: c.created_at
        })) || []
      }

      setEstatisticasGerais(stats)
    } catch (error) {
      console.error('Erro ao carregar estat√≠sticas gerais:', error)
    }
  }

  const carregarRelatorioFinanceiro = async () => {
    try {
      const dataLimite = new Date()
      dataLimite.setDate(dataLimite.getDate() - parseInt(periodoFiltro))

      // Carregar or√ßamentos do per√≠odo
      const { data: orcamentos } = await supabase
        .from('orcamentos')
        .select('*')
        .eq('user_id', profile?.id)
        .gte('created_at', dataLimite.toISOString())
        .order('created_at', { ascending: false })

      // Carregar receitas com c√°lculos
      const { data: receitas } = await supabase
        .from('receitas')
        .select('nome, custo_calculado, preco_sugerido')
        .eq('user_id', profile?.id)
        .not('custo_calculado', 'is', null)
        .not('preco_sugerido', 'is', null)

      // Carregar ingredientes para contagem
      const { data: ingredientes } = await supabase
        .from('ingredientes')
        .select('id')
        .eq('user_id', profile?.id)

      // Carregar c√°lculos do per√≠odo
      const { data: calculos } = await supabase
        .from('calculos_salvos')
        .select('*')
        .eq('user_id', profile?.id)
        .gte('created_at', dataLimite.toISOString())

      // Processar dados
      const valorMedioOrcamento = orcamentos?.length ? 
        orcamentos.reduce((sum, o) => sum + o.valor_total, 0) / orcamentos.length : 0

      const margemMediaLucro = calculos?.length ?
        calculos.reduce((sum, c) => {
          const detalhes = c.detalhes as any
          return sum + (detalhes?.margemLucro || 0)
        }, 0) / calculos.length : 0

      // Receitas mais rent√°veis
      const receitasRentaveis = receitas?.map(r => ({
        nome: r.nome,
        custo: r.custo_calculado || 0,
        preco: r.preco_sugerido || 0,
        margem: r.custo_calculado && r.preco_sugerido ? 
          ((r.preco_sugerido - r.custo_calculado) / r.preco_sugerido) * 100 : 0
      })).sort((a, b) => b.margem - a.margem).slice(0, 5) || []

      // Tend√™ncia de vendas (√∫ltimos 6 meses)
      const tendenciaVendas = []
      for (let i = 5; i >= 0; i--) {
        const mesData = new Date()
        mesData.setMonth(mesData.getMonth() - i)
        const inicioMes = new Date(mesData.getFullYear(), mesData.getMonth(), 1)
        const fimMes = new Date(mesData.getFullYear(), mesData.getMonth() + 1, 0)

        const orcamentosMes = orcamentos?.filter(o => {
          const dataOrcamento = new Date(o.created_at)
          return dataOrcamento >= inicioMes && dataOrcamento <= fimMes
        }) || []

        tendenciaVendas.push({
          mes: mesData.toLocaleDateString('pt-BR', { month: 'short' }),
          valor: orcamentosMes.reduce((sum, o) => sum + o.valor_total, 0)
        })
      }

      const relatorio: RelatorioFinanceiro = {
        periodo: `√öltimos ${periodoFiltro} dias`,
        totalReceitas: receitas?.length || 0,
        totalOrcamentos: orcamentos?.length || 0,
        valorMedioOrcamento,
        receitasCalculadas: receitas?.length || 0,
        margemMediaLucro: margemMediaLucro * 100, // converter para %
        ingredientesCadastrados: ingredientes?.length || 0,
        receitasMaisRentaveis: receitasRentaveis,
        tendenciaVendas
      }

      setRelatorioFinanceiro(relatorio)
    } catch (error) {
      console.error('Erro ao carregar relat√≥rio financeiro:', error)
    }
  }

  const carregarRelatorioIngredientes = async () => {
    try {
      // Carregar ingredientes com uso
      const { data: receitaIngredientes } = await supabase
        .from('receita_ingredientes')
        .select(`
          quantidade,
          ingredientes (id, nome, preco_kg, categoria:categorias_ingredientes(nome, cor)),
          receitas!inner (user_id)
        `)
        .eq('receitas.user_id', profile?.id)

      // Processar ingredientes mais usados
      const usoIngredientes: { [key: string]: { nome: string, categoria: string, usos: number, custoMedio: number } } = {}

      receitaIngredientes?.forEach(ri => {
        const ing = (ri as any).ingredientes
        if (ing) {
          const key = ing.id
          if (!usoIngredientes[key]) {
            usoIngredientes[key] = {
              nome: ing.nome,
              categoria: ing.categoria?.nome || 'Sem categoria',
              usos: 0,
              custoMedio: ing.preco_kg
            }
          }
          usoIngredientes[key].usos++
        }
      })

      const ingredientesMaisUsados = Object.values(usoIngredientes)
        .sort((a, b) => b.usos - a.usos)
        .slice(0, 10)

      // Carregar categorias com contagem
      const { data: categorias } = await supabase
        .from('categorias_ingredientes')
        .select(`
          nome, cor,
          ingredientes (id)
        `)
        .eq('user_id', profile?.id)

      const categoriasMaisUsadas = categorias?.map(cat => ({
        nome: cat.nome,
        quantidade: (cat as any).ingredientes?.length || 0,
        cor: cat.cor
      })).sort((a, b) => b.quantidade - a.quantidade).slice(0, 5) || []

      // Ingredientes mais caros
      const { data: ingredientesCaros } = await supabase
        .from('ingredientes')
        .select(`
          nome, preco_kg,
          categoria:categorias_ingredientes(nome)
        `)
        .eq('user_id', profile?.id)
        .eq('ativo', true)
        .order('preco_kg', { ascending: false })
        .limit(10)

      const relatorio: RelatorioIngredientes = {
        ingredientesMaisUsados,
        categoriasMaisUsadas,
        ingredientesCaros: ingredientesCaros?.map(ing => ({
          nome: ing.nome,
          preco: ing.preco_kg,
          categoria: (ing as any).categoria?.nome || 'Sem categoria'
        })) || []
      }

      setRelatorioIngredientes(relatorio)
    } catch (error) {
      console.error('Erro ao carregar relat√≥rio de ingredientes:', error)
    }
  }

  const exportarRelatorio = (tipo: string) => {
    const data = new Date().toLocaleDateString('pt-BR')
    let htmlContent = ''

    if (tipo === 'financeiro' && relatorioFinanceiro) {
      htmlContent = `
      <!DOCTYPE html>
      <html>
      <head>
